mapper.select:
select order_id, member_id, restaurant_id, order_time, expect_time, price, freight, `state`, address_id
from order_info
where member_id = #{memberId, jdbcType=INTEGER}

select sum(price)
from order_info
where member_id = #{memberId, jdbcType=INTEGER}

select order_id, member_id, restaurant_id, order_time, expect_time, price, freight, `state`, address_id
from order_info
where restaurant_id = #{restaurantId, jdbcType=VARCHAR}
