mapper.delete:

delete from order_product
where order_id = #{orderId,jdbcType=INTEGER}
  and food_name = #{foodName,jdbcType=VARCHAR}

mapper.update:

update order_product
set restaurant_id = #{restaurantId,jdbcType=VARCHAR},
  food_quantity = #{foodQuantity,jdbcType=INTEGER},
  food_price = #{foodPrice,jdbcType=DOUBLE},
  food_id = #{foodId,jdbcType=INTEGER}
where order_id = #{orderId,jdbcType=INTEGER}
  and food_name = #{foodName,jdbcType=VARCHAR}

mapper.select:

select order_id, food_name, restaurant_id, food_quantity, food_price, food_id
from order_product
where order_id = #{orderId,jdbcType=INTEGER}
  and food_name = #{foodName,jdbcType=VARCHAR}

select order_id, food_name, restaurant_id, food_quantity, food_price, food_id
from order_product
where order_id = #{orderId,jdbcType=INTEGER}
